import { createSlice } from "@reduxjs/toolkit";
import { uiActions } from "./ui-slice";

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    items: [],
    totalQuantity: 0,
  },
  reducers: {
    addItem: (state, action) => {
      const newItem = action.payload;
      const existingItem = state.items.find((item) => item.id === newItem.id);
      if (!existingItem) {
        state.items.push({
          id: newItem.id,
          price: newItem.price,
          quantity: 1,
          totalPrice: newItem.price,
          title: newItem.title,
        });
      } else {
        existingItem.totalPrice += existingItem.price;
        existingItem.quantity++;
      }
      state.totalQuantity++;
    },
    removeItem: (state, action) => {
      const removeItemId = action.payload.id;
      const removeItem = state.items.find((item) => item.id === removeItemId);
      removeItem.quantity--;
      removeItem.totalPrice -= removeItem.price;
      if (removeItem.quantity === 0) {
        const filteredItems = state.items.filter(
          (item) => item.id !== removeItemId
        );
        state.items = filteredItems;
      }
      state.totalQuantity--;
    },
  },
});

const sendCartData = (cartData) => {
  return async (dispatch) => {
    dispatch(
      uiActions.setNotification({
        status: "pending",
        title: "sending...",
        message: "Sending cart data!",
      })
    );

    const sendRequest = async () => {
      const response = await fetch(
        "https://react-http-ee2ff-default-rtdb.firebaseio.com/cart.json",
        {
          method: "POST",
          body: JSON.stringify(cartData),
        }
      );

      if (!response.ok) {
        throw new Error("Sending cart data failed.");
      }
    };

    try {
      await sendRequest();
      dispatch(
        uiActions.setNotification({
          status: "success",
          title: "Success!",
          message: "Sending cart data successfully!",
        })
      );
    } catch (error) {
      dispatch(
        uiActions.setNotification({
          status: "error",
          title: "Error!",
          message: "Sending cart data failed!",
        })
      );
    }
  };
};

export const cartReducer = cartSlice.reducer;

export const cartActions = cartSlice.actions;
