import { useEffect } from "react";
import useHttp from "../components/hooks/use-http";
import QuetoList from "../components/quotes/QuoteList";
import { getAllQuotes } from "../lib/api";
import LoadingSpinner from "../components/UI/LoadingSpinner";

const DUMMY_QUOTES = [
  { id: "q1", author: "Max", text: "Learn React" },
  { id: "q2", author: "James", text: "Learn Vue" },
];

const AllQuotes = () => {
  const {
    sendRequest,
    status,
    data: loadedQuote,
  } = useHttp(getAllQuotes, true);

  useEffect(() => {
    sendRequest();
  }, [sendRequest]);

  if (status === "pending") return <LoadingSpinner></LoadingSpinner>;

  if (status === "completed")
    return <QuetoList quotes={loadedQuote}></QuetoList>;
};

export default AllQuotes;
