import { Fragment } from "react";
import { useLocation, useParams } from "react-router";
import { Route } from "react-router";
import NoQuoteFound from "../components/quotes/NoQuotesFound";
import Comments from "../components/comments/Comments";
import HighlightedQuote from "../components/quotes/HighlightedQuote";
import { Link } from "react-router-dom";

const DUMMY_QUOTES = [
  { id: "q1", author: "Max", text: "Learn React" },
  { id: "q2", author: "James", text: "Learn Vue" },
];

const QuoteDetail = () => {
  const { quoteId: id } = useParams();

  const location = useLocation();

  console.log(location.get());

  const quote = DUMMY_QUOTES.find((quote) => {
    return quote.id === id;
  });

  if (!quote) return <NoQuoteFound />;

  const isCommentsOpen = 0;

  return (
    <Fragment>
      <HighlightedQuote text={quote.text} author={quote.author} />

      <div className="centered">
        <Link to={`/quotes/${id}/comments`}>Open Comments</Link>
      </div>
      <Route path={`/quotes/${id}/comments`}>
        <Comments />
      </Route>
    </Fragment>
  );
};

export default QuoteDetail;
